import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { firstValueFrom, lastValueFrom } from 'rxjs';

interface ISignupComponent {
  Firstname: string
  Lastname: string
  Email: string
  Passwordhash: string
  Username: string
}

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit {

  // type: string = "password";
  public Firstname = ''
  public Lastname = ''
  public Email = ''
  public Username = ''
  public Passwordhash = ''
  public SignupComponents: ISignupComponent[] = [
    {
      Firstname: 'joe',
      Lastname: 'johnson',
      Email: 'qwsd',
      Passwordhash: '123',
      Username: '321'
    }

  ]


  // isText: boolean = false;
  // eyeIcon: string= "fa fa-eye";

  // hideShowPass(){

  //   this.isText = !this.isText;
  //   this.isText ? this.eyeIcon = "fa-eye-slash" : this.eyeIcon = "fa-eye";
  //   this.isText ? this.type = "text" : this.type = "password";
  // }

  async ngOnInit() {
    await this.loadUsers()
  }

  constructor(
   private httpClient : HttpClient
  ) {}

  async loadUsers() {
   this.SignupComponents = await firstValueFrom(this.httpClient.get<ISignupComponent[]>('/api/users'))
  }

  async addUser(){
    this.httpClient.delete('/api/users/Joe')
    this.httpClient.post('/api/users', {
      Firstname: this.Firstname,
      Lastname: this.Lastname,
      Email: this.Email,
      Passwordhash: this.Passwordhash,
      Username: this.Username
    })

    this.loadUsers()

    this.SignupComponents.push({
      Firstname: this.Firstname,
      Lastname: this.Lastname,
      Email: this.Email,
      Passwordhash: this.Passwordhash,
      Username: this.Username
    })
    await this.loadUsers()
    this.Firstname = ''
    this.Lastname = ''
    this.Email = ''
    this.Passwordhash = ''
    this.Username = ''
  }

}


<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-med-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-white">
                    <div class="panel-heading">
                        <h3 class="pt-3 font-weight-bold">Register for CampUs</h3>
                    </div>
                    <div class="panel-body p-3">
                        <form>
                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <span class="fa fa-user p-2"></span>
                                    <input [(ngModel)]="Firstname" type="text" placeholder="First Name" [ngModelOptions]="{standalone: true}">
                                </div>
                            </div>
                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <span class="fa fa-user p-2"></span>
                                    <input [(ngModel)]="Lastname" type="text" placeholder="Last Name" [ngModelOptions]="{standalone: true}">
                                </div>
                            </div>
                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <span class="fa fa-user p-2"></span>
                                    <input [(ngModel)]="Email" type="text" placeholder="Email" [ngModelOptions]="{standalone: true}">
                                </div>
                            </div>
                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <span class="fa fa-user p-2"></span>
                                    <input [(ngModel)]="Username" type="text" placeholder="Username" [ngModelOptions]="{standalone: true}">
                                </div>
                            </div>
                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <span class="fa fa-lock p-2"></span>
                                    <input [(ngModel)]="Passwordhash" type="text" placeholder="Password" [ngModelOptions]="{standalone: true}">
                                    <!-- <span (click)="hideShowPass()" class="fa {{eyeIcon}}"></span> -->
                                </div>
                            </div>

                            <!-- <button type= "submit" class="btn btn-primary btn-block mt-4 w-100">Login</button> -->
                            <!-- <button type="button" routerLink="/login" click class="btn btn-primary btn-block mt-4 w-100">Register</button>     -->
                            <button (click)="addUser()" class="btn btn-primary btn-block mt-4 w-100">Register</button>
                        </form>
                        <div>Title</div>
                        <div *ngFor="let currentsignupcomp of SignupComponents">
                            <div>{{currentsignupcomp.Firstname}}</div>
                            <div>{{currentsignupcomp.Lastname}}</div>
                            <div>{{currentsignupcomp.Username}}</div>
                            <div>{{currentsignupcomp.Email}}</div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
