import { Component } from '@angular/core';

interface ISignupComponent {
  First_Name: string
  Last_Name: string
  Email: string
  Password: string
  Username: string
}

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {

  // type: string = "password";
  public First_Name = ''
  public Last_Name = ''
  public Email = ''
  public Password = ''
  public Username = ''
  public SignupComponents: ISignupComponent[] = [
    {
      First_Name: 'joe',
      Last_Name: 'johnson',
      Email: 'qwsd',
      Password: '123',
      Username: '321'
    }

  ]


  // isText: boolean = false;
  // eyeIcon: string= "fa fa-eye";

  // hideShowPass(){

  //   this.isText = !this.isText;
  //   this.isText ? this.eyeIcon = "fa-eye-slash" : this.eyeIcon = "fa-eye";
  //   this.isText ? this.type = "text" : this.type = "password";
  // }

  addUser(){
    this.SignupComponents.push({
      First_Name: this.First_Name,
      Last_Name: this.Last_Name,
      Email: this.Email,
      Password: this.Password,
      Username: this.Username
    })
    this.First_Name = ''
    this.Last_Name = ''
    this.Email = ''
    this.Password = ''
    this.Username = ''
  }

}
